(function($){$.fn.accordion=function(options){var defaults={maxWidth:"620px",minWidth:"140px",lazyload:true,speed:"1000"};var opts=$.extend(defaults,options);$(this).each(function(){var t=$(this),m=t.children(),c=m.children();m.find(".active a").hide();c.on("mouseenter",function(){if(opts.lazyload==true&&typeof($(this).attr("data-src"))!=="undefined"){var src=$(this).attr("data-src");$(this).css("background-image","url("+src+")").removeAttr("data-src")}$(this).addClass("active").animate({width:opts.maxWidth},opts.speed).children().fadeOut(opts.speed).parent().siblings().removeClass("active").animate({width:opts.minWidth},opts.speed).children().show(opts.speed)})})}})(jQuery);
